<?phprequire_once "configs/config.php";function text_get(){	return file_get_contents('data/text.txt');}function text_set($text){	file_put_contents('data/text.txt', $text);}function getAll($link, $table, $orderby = 'id'){    $query = "SELECT * FROM {$table} order by {$orderby} desc";    $result = mysqli_query($link, $query);    if (!$result)        die(mysqli_error($link));    $n = mysqli_num_rows($result);    $res = array();    for ($i = 0; $i < $n; $i++) {        $row = mysqli_fetch_assoc($result);        $res[] = $row;    }    return $res;};function newUser($link, $name, $login, $email, $pass){    $t = "INSERT INTO user (name, login, email, pass) VALUES ('%s','%s','%s','%s')";    $query = sprintf($t, mysqli_real_escape_string($link, $name), mysqli_real_escape_string($link, $login), mysqli_real_escape_string($link, $email), mysqli_real_escape_string($link, $pass));    $result = mysqli_query($link, $query);    if (!$result) {        die(mysqli_error($link));    }    return true;};function getOne($link, $id, $table){    $query = sprintf("SELECT * FROM {$table} where id=%d", (int)$id);    $result = mysqli_query($link, $query);    if (!$result)        die(mysqli_error($link));    $res = mysqli_fetch_assoc($result);    return $res;};